import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http'

@Injectable({
  providedIn: 'root'
})
export class ProductService {


  url="http://localhost:9092/"

  constructor(private http:HttpClient) { }

  getlogin(email: any)
  {
    return this.http.get(this.url+"users/"+email);

  }

  postregister(data: any)
  {
    return this.http.post(this.url+"users",data);

  }
  postnote(data: any)
  {
    return this.http.post(this.url+"notes",data);

  }
  getNotes()
  {
    return this.http.get(this.url+"notes");

  }
  postApprovedReview(data: any)
  {
    return this.http.post(this.url+"acceptReview",data);

  }
  postReview(data: any)
  {
    return this.http.post(this.url+"reviews",data);

  }

  getProducts()
  {
    return this.http.get(this.url+"products");

  }
  getApprovedReview(){

    return this.http.get(this.url+"askreview/");
  }
  deleteApprovedReview(id:any){
    return this.http.delete(this.url+"denyReview/"+id)
  }
  getReview(id: any){

    return this.http.get(this.url+"review/id/"+id);
  }
  getUserCount()
  {
    return this.http.get(this.url+"users/count");

  }
  getReviewCount()
  {
    return this.http.get(this.url+"review/count");

  }
  getProductCount()
  {
    return this.http.get(this.url+"products/count");

  }
  getCode(code: any)
  {
    return this.http.get(this.url+"products/code/"+code);
  }

  getPrice(price: any)
  {
    return this.http.get(this.url+"products/price/"+price);
  }

  getName(name: any)
  {
    return this.http.get(this.url+"products/name/"+name);
  }

  getBrand(brand: any)
  {
    return this.http.get(this.url+"products/brand/"+brand);
  }

  getID(id: any)
  {
    return this.http.get(this.url+"products/id/"+id);
  }



}
